
public class ArrayIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]=new int[5];
		a[0]=1;
		a[1]=2;
		a[2]=5;
		a[3]=6;
		a[4]=7;
		int sum=0;
		int i;
		for(i=0;i<a.length;i++)
		{
				sum=sum+a[i];
			
					}
		System.out.println(sum);
				
		

	}

}
